      out.write("                            aclModuleList: result.data,\n");
      out.write("                            \"showDownAngle\": function () {\n");
      out.write("                                return function (text, render) {\n");
      out.write("                                    return (this.aclModuleList && this.aclModuleList.length > 0) ? \"\" : \"hidden\";\n");
      out.write("                                }\n");
      out.write("                            },\n");
      out.write("                            \"displayClass\": function () {\n");
      out.write("                                return \"\";\n");
      out.write("                            }\n");
      out.write("                        });\n");
      out.write("                        $(\"#aclModuleList\").html(rendered);\n");
      out.write("                        recursiveRenderAclModule(result.data);\n");
      out.write("                        bindAclModuleClick();\n");
      out.write("                    } else {\n");
      out.write("                        showMessage(\"加载权限模块\", result.msg, false);\n");
      out.write("                    }\n");
      out.write("                }\n");
      out.write("            })\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        $(\".aclModule-add\").click(function () {\n");
      out.write("            $(\"#dialog-aclModule-form\").dialog({\n");
      out.write("                model: true,\n");
      out.write("                title: \"新增权限模块\",\n");
      out.write("                open: function(event, ui) {\n");
      out.write("                    $(\".ui-dialog-titlebar-close\", $(this).parent()).hide();\n");
      out.write("                    optionStr = \"<option value=\\\"0\\\">-</option>\";\n");
      out.write("                    recursiveRenderAclModuleSelect(aclModuleList, 1);\n");
      out.write("                    $(\"#aclModuleForm\")[0].reset();\n");
      out.write("                    $(\"#parentId\").html(optionStr);\n");
      out.write("                },\n");
      out.write("                buttons : {\n");
      out.write("                    \"添加\": function(e) {\n");
      out.write("                        e.preventDefault();\n");
      out.write("                        updateAclModule(true, function (data) {\n");
      out.write("                            $(\"#dialog-aclModule-form\").dialog(\"close\");\n");
      out.write("                        }, function (data) {\n");
      out.write("                            showMessage(\"新增权限模块\", data.msg, false);\n");
      out.write("                        })\n");
      out.write("                    },\n");
