      out.write("                        $(\"#dialog-aclModule-form\").dialog(\"close\");\n");
      out.write("                    }\n");
      out.write("                }\n");
      out.write("            });\n");
      out.write("        });\n");
      out.write("        function updateAclModule(isCreate, successCallback, failCallback) {\n");
      out.write("            $.ajax({\n");
