      out.write("                        }\n");
      out.write("                    }\n");
      out.write("                }\n");
      out.write("            })\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        function recursiveRenderAclModuleSelect(aclModuleList, level) {\n");
      out.write("            level = level | 0;\n");
      out.write("            if (aclModuleList && aclModuleList.length > 0) {\n");
      out.write("                $(aclModuleList).each(function (i, aclModule) {\n");
