      out.write("                        for(var j = 3; j <= level; j++) {\n");
      out.write("                            blank += \"..\";\n");
      out.write("                        }\n");
      out.write("                        blank += \"∟\";\n");
      out.write("                    }\n");
      out.write("                    optionStr += Mustache.render(\"<option value='{{id}}'>{{name}}</option>\", {id: aclModule.id, name: blank + aclModule.name});\n");
      out.write("                    if (aclModule.aclModuleList && aclModule.aclModuleList.length > 0) {\n");
      out.write("                        recursiveRenderAclModuleSelect(aclModule.aclModuleList, level + 1);\n");
      out.write("                    }\n");
      out.write("                });\n");
      out.write("            }\n");
      out.write("        }\n");
      out.write("\n");
      out.write("        function recursiveRenderAclModule(aclModuleList) {\n");
      out.write("            if (aclModuleList && aclModuleList.length > 0) {\n");
      out.write("                $(aclModuleList).each(function (i, aclModule) {\n");
      out.write("                    aclModuleMap[aclModule.id] = aclModule;\n");
      out.write("                    if (aclModule.aclModuleList && aclModule.aclModuleList.length > 0) {\n");
      out.write("                        var rendered = Mustache.render(aclModuleListTemplate, {\n");
      out.write("                            aclModuleList: aclModule.aclModuleList,\n");
      out.write("                            \"showDownAngle\": function () {\n");
      out.write("                                return function (text, render) {\n");
      out.write("                                    return (this.aclModuleList && this.aclModuleList.length > 0) ? \"\" : \"hidden\";\n");
      out.write("                                }\n");
      out.write("                            },\n");
      out.write("                            \"displayClass\": function () {\n");
      out.write("                                return \"hidden\";\n");
      out.write("                            }\n");
      out.write("                        });\n");
      out.write("                        $(\"#aclModule_\" + aclModule.id).append(rendered);\n");
      out.write("                        recursiveRenderAclModule(aclModule.aclModuleList);\n");
